class ToggleFriendship{constructor(t){this.toggler=t,this.toggleFriendship()}toggleFriendship(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"GET",url:$(e).attr("href")}).done((function(t){1==t.data.friendAdded?($(" button",e).html("REMOVE FRIEND"),new Noty({theme:"relax",text:"Added to your friend list",type:"success",layout:"topRight",timeout:1500}).show()):($(" button",e).html("ADD FRIEND"),new Noty({theme:"relax",text:"Removed from your friend list",type:"error",layout:"topRight",timeout:1500}).show())})).fail((function(t){console.log("error in completing the request",t)}))}))}}