console.log("js file of toggle_friendship is loaded");class ToggleFriendship{constructor(t){this.toggler=t,this.toggleFriendship()}toggleFriendship(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"GET",url:$(e).attr("href")}).done((function(t){console.log(t,"******"),1==t.data.friendAdded?$(" button",e).html("REMOVE FRIEND"):$(" button",e).html("ADD FRIEND")})).fail((function(t){console.log("error in completing the request",t)}))}))}}